import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { RiCloseCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';



const Exercise = props => (
  <tr>
    <td>{props.todo.username}</td>
    <td>{props.todo.description}</td>
    <td>{props.todo.duration}</td>
    <td>{props.todo.date.substring(0,10)}</td>
    <td>{props.todo.ddate.substring(0,10)}</td>
    <td>
      <Link to={"/edit/"+props.todo._id}><TiEdit className="icon-edit" /></Link>  <RiCloseCircleLine className="icon-delete" onClick={() => { props.deleteTodo(props.todo._id) }} />
    </td>
  </tr>
)

export default class TodosList extends Component {
  constructor(props) {
    super(props);

    this.deleteTodo = this.deleteTodo.bind(this)

    this.state = {exercises: []};
  }

  componentDidMount() {
    axios.get('http://localhost:3001/todos/')
      .then(response => {
        this.setState({ exercises: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteTodo(id) {
    axios.delete('http://localhost:3001/todos/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      exercises: this.state.exercises.filter(el => el._id !== id)
    })
  }

  todoList() {
    return this.state.exercises.map(currenttodo => {
      return <Exercise todo={currenttodo} deleteTodo={this.deleteTodo} key={currenttodo._id}/>;
    })
  }

  render() {
    return (

      <div className="table-wrap">
        <h3>Активные задачи</h3>
        <table className="table-2">
          <thead className="thead-dark">
            <tr>
              <th>Пользователь</th>
              <th>Описание задачи</th>
              <th>Продолжительность</th>
              <th>Начало</th>
              <th>Окончание</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            { this.todoList() }
          </tbody>
        </table>
      </div>
    )
  }
}
